import React, { useEffect, useState } from "react";
import { WeatherCard } from "./components/WeatherCard";
import { Footer } from "./components/Footer";
import { TodayWeatherCard } from "./components/TodayWeatherCard";
import { GetWeahterData, WeatherData } from "./api/WeatherApi";

function App() {
  //useEffect(() => {//isviekcia api call ir nusettina state}, []);
  // Isivykdo kai komponentas yra uzkraunamas arba kai state yra pakeiciamas
  //

  const [weatherData, setWeatherData] = useState<WeatherData | null>();
  const [weatherArrayTimeIndex, setWeatherArrayTimeIndex] = useState<number>(0);
  const [weatherArrayDateIndex, setWeatherArrayDateIndex] = useState<number>(0);
  const [weatherDate, setWeatherDate] = useState<string>("");
  const [finalDate, setFinalDate] = useState<string>("");
  const [tomorrowDateIndex, settomorrowDateIndex] = useState<number>(0);

  useEffect(() => {
    const fetchData = async () => {
      let response = await GetWeahterData();
      setWeatherData(response);
    };
    let dateToday: string;
    let today = new Date();
    let timeNow = today.getHours();
    setWeatherArrayTimeIndex(timeNow - 1);

    dateToday =
      today.getFullYear() +
      "-0" +
      (today.getMonth() + 1) +
      "-" +
      today.getDate() +
      "T12:00";

    setWeatherDate(weatherData?.hourly.time[weatherArrayDateIndex] ?? "00-00");

    // for (let i = weatherArrayDateIndex; i < weatherArrayDateIndex + 6; i++) {}
    let myValue;
    myValue = weatherData?.hourly.time.indexOf(dateToday);
    console.log(weatherData?.hourly.time.indexOf(dateToday));
    // myValue.split("T")[0].split("2023-")[1];
    // setTomorrowDateIndex(myValue);

    console.log(finalDate);

    fetchData();
  }, []);

  return (
    <>
      <TodayWeatherCard
        temperature={
          weatherData?.hourly.temperature_2m[weatherArrayTimeIndex] ?? 0
        }
      />
      <WeatherCard
        temperature={
          weatherData?.hourly.temperature_2m[weatherArrayDateIndex + 24] ?? 0
        }
        date={weatherData?.hourly.time[tomorrowDateIndex + 24] ?? ""}
      />
      <WeatherCard
        temperature={
          weatherData?.hourly.temperature_2m[weatherArrayDateIndex + 48] ?? 0
        }
        date={weatherData?.hourly.time[tomorrowDateIndex + 48] ?? ""}
      />
      <WeatherCard
        temperature={
          weatherData?.hourly.temperature_2m[weatherArrayDateIndex + 72] ?? 0
        }
        date={weatherData?.hourly.time[tomorrowDateIndex + 72] ?? ""}
      />
      <WeatherCard
        temperature={
          weatherData?.hourly.temperature_2m[weatherArrayDateIndex + 96] ?? 0
        }
        date={weatherData?.hourly.time[tomorrowDateIndex + 96] ?? ""}
      />
      <WeatherCard
        temperature={
          weatherData?.hourly.temperature_2m[weatherArrayDateIndex + 120] ?? 0
        }
        date={weatherData?.hourly.time[tomorrowDateIndex + 120] ?? ""}
      />
      <WeatherCard
        temperature={
          weatherData?.hourly.temperature_2m[weatherArrayDateIndex + 144] ?? 0
        }
        date={weatherData?.hourly.time[tomorrowDateIndex + 144] ?? ""}
      />
      <Footer />
    </>
  );
}

export default App;
