import { useState } from "react";
import { CityCoordinates } from "../api/CoordinatesApi";

export const CityInput = () => {
  const [city, setCity] = useState("");
  const [returnCity, setReturnCity] = useState("");

  const handleSubmit = (event: { preventDefault: () => void }) => {
    event.preventDefault();
    let formatedCity =
      city.toLowerCase().charAt(0).toUpperCase() + city.slice(1);
    setReturnCity(formatedCity);
    CityCoordinates(returnCity);
    setCity("");
    console.log(city);
    console.log(returnCity);
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          id="cityName"
          onChange={(event) => setCity(event.target.value)}
        />
        <input type="submit" id="submitInpt" />
      </form>
    </>
  );
};
