  //

  const [weatherData, setWeatherData] = useState<WeatherData | null>();
  const [weatherArrayTimeIndex, setweatherArrayTimeIndex] = useState<number>(0);
  const [weatherArrayDayIndex, setweatherArrayDayIndex] =
    useState<string>("0000-00-00");

  useEffect(() => {
    const fetchData = async () => {
      let response = await GetWeahterData();
      setWeatherData(response);
    };
    let date: string;
    let today = new Date();
    let timeNow = today.getHours();
    setweatherArrayTimeIndex(timeNow - 1);

    if (today.getMonth() < 10) {
      date =
        today.getFullYear() + "-0" + today.getMonth() + "-" + today.getDate();
    } else if (today.getMonth() >= 10) {
      date =
        today.getFullYear() + "-" + today.getMonth() + "-" + today.getDate();
    }

    fetchData();
  }, []);
